INSERT INTO `serialized_object` VALUES ('', 'Clinical-CareCardFollowUp', 'Dataset from the inside of the Care Card', 'org.openmrs.module.reporting.dataset.definition.DataSetDefinition', 'org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition', 'org.openmrs.module.reporting.serializer.ReportingSerializer', '<org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition id=\"1\" uuid=\"9872d016-30c5-48f4-8115-5590e2a9dc8d\" retired=\"false\">\n  <name>Clinical-CareCardFollowUp</name>\n  <description>Dataset from the inside of the Care Card</description>\n  <creator id=\"2\" uuid=\"1c3db49d-440a-11e6-a65c-00e04c680037\"/>\n  <dateCreated id=\"3\">2019-07-01 11:38:12 UTC</dateCreated>\n  <changedBy reference=\"2\"/>\n  <dateChanged id=\"4\">2020-09-28 04:52:30 UTC</dateChanged>\n  <parameters id=\"5\">\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"6\">\n      <name>startDate</name>\n      <label>Start Date</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"7\">\n      <name>endDate</name>\n      <label>End Date</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n  </parameters>\n  <id>32</id>\n  <sqlQuery>SELECT&#xd;\nglobal_property.property_value as DatimCode,&#xd;\npid1.identifier as ANCNo,&#xd;\npid2.identifier as PepfarID,&#xd;\npid3.identifier as HospID,&#xd;\nDATE_FORMAT(enc.encounter_datetime,&apos;%d-%b-%Y&apos;) &#xd;\nas VisitDate,&#xd;\nvisit.visit_id &#xd;\nas VisitID,&#xd;\nMAX(IF(obs.concept_id=165050,cn1.name, NULL))&#xd;\nas `PregnancyBreasfeedingStatus`,&#xd;\nMAX(IF(obs.concept_id=5596,DATE_FORMAT(obs.value_datetime,&apos;%d-%b-%Y&apos;), NULL))&#xd;\nas `ExpectedDateOfDelivery`,&#xd;\nMAX(IF(obs.concept_id=5089,obs.value_numeric, NULL))&#xd;\nas `WeightKg`,&#xd;\nMAX(IF(obs.concept_id=5090,obs.value_numeric, NULL))&#xd;\nas `Heightcm`,&#xd;\nMAX(IF(obs.concept_id=5085,obs.value_numeric, NULL)) &#xd;\nas `BPSystolic`,&#xd;\nMAX(IF(obs.concept_id=5086,obs.value_numeric, NULL)) &#xd;\nas `BPDystolic`,&#xd;\nMAX(IF(obs.concept_id=5088,obs.value_numeric, NULL)) &#xd;\nas `Temp`,&#xd;\nMAX(IF(obs.concept_id=5087,obs.value_numeric, NULL))&#xd;\nas `Pulse`,&#xd;\nMAX(IF(obs.concept_id=1342,obs.value_numeric, NULL))&#xd;\nas `BodyMassIndex`,&#xd;\nMAX(IF(obs.concept_id=165935,cn1.name, NULL))&#xd;\nas `MUACInterpretation`,&#xd;\nMAX(IF(obs.concept_id=165243,obs.value_numeric, NULL))&#xd;\nas `BMI_MUAC`,&#xd;\nMAX(IF(obs.concept_id=5271,obs.value_coded, NULL))&#xd;\nas `IsPatientOnFamilyPlanning`,&#xd;\nMAX(IF(obs.concept_id=374,cn1.name, NULL))&#xd;\nas `FamilyPlanningMethod`,&#xd;\nMAX(IF(obs.concept_id=165039,cn1.name, NULL))&#xd;\nas `FunctionalStatus`,&#xd;\nMAX(IF(obs.concept_id=5356,cn1.name, NULL))&#xd;\nas `WHOClinicalStage`,&#xd;\nMAX(IF(obs.concept_id=1659,cn1.name, NULL))&#xd;\nas `TBStatus`,&#xd;\nMAX(IF(obs.concept_id=1659,DATE_FORMAT(obs.obs_datetime,&apos;%d-%b-%Y&apos;), NULL)) as `TBStatusDate`,&#xd;\nMAX(IF(obs.concept_id=165995,cn1.name, NULL))&#xd;\nas `MethodOfTBDiagnosis`,&#xd;\nGROUP_CONCAT(IF(obs.concept_id=160170,cn1.name, NULL))&#xd;\nas `OtherOIs`,&#xd;\nMAX(IF(obs.concept_id=165613,obs.value_text, NULL))&#xd;\nas `OtherOISpecify`,&#xd;\nGROUP_CONCAT(IF(obs.concept_id=159935,cn1.name, NULL))&#xd;\nas `NotedSideEffects`,&#xd;\nMAX(IF(obs.concept_id=165593,obs.value_text, NULL))&#xd;\nas `OtherNotedSideEffects`,&#xd;\nMAX(IF(obs.concept_id=165771,cn1.name, NULL))&#xd;\nas `ARVMedicationPlan`,&#xd;\nMAX(IF(obs.concept_id=1252,cn1.name, NULL))&#xd;\nas `ReasonForRegimenStop`,&#xd;\nMAX(IF(obs.concept_id=165056,cn1.name, NULL))&#xd;\nas `ReasonForRegimenSubstitutionSwitch`,&#xd;\nMAX(IF(obs.concept_id=165708,cn1.name, NULL))&#xd;\nas `RegimenLine`,&#xd;\nMAX(IF(obs.concept_id IN(164506,164507),cn1.name, NULL))&#xd;\nas `FirstLineRegimen`,&#xd;\nMAX(IF(obs.concept_id IN(164513,164514),cn1.name, NULL))&#xd;\nas `SecondLineRegimen`,&#xd;\nMAX(IF(obs.concept_id IN(165702,165703),cn1.name, NULL))&#xd;\nas `ThirdLineRegimen`,&#xd;\nMAX(IF(obs.concept_id=165290,cn1.name, NULL))&#xd;\nas `ARVAdherence`,&#xd;\nMAX(IF(obs.concept_id=166130,cn1.name, NULL))&#xd;\nas `ReasonForPoorARVAdherence`,&#xd;\nMAX(IF(obs.concept_id=165069,cn1.name, NULL))&#xd;\nas `CotrimoxazoleDose`,&#xd;\nMAX(IF(obs.concept_id=166131,cn1.name, NULL))&#xd;\nas `ReasonForPoorCotrimoxazoleAdherence`,&#xd;\nMAX(IF(obs.concept_id=161652,cn1.name, NULL))&#xd;\nas `CotrimoxazoleAdherence`,&#xd;\nMAX(IF(obs.concept_id=165080,cn1.name, NULL))&#xd;\nas `INHDose`,&#xd;\nMAX(IF(obs.concept_id=161653,cn1.name, NULL))&#xd;\nas `INHAdherence`,&#xd;\nMAX(IF(obs.concept_id=166132,cn1.name, NULL))&#xd;\nas `ReasonForPoorINHAdherence`,&#xd;\nMAX(IF(obs.concept_id= 163101,obs.value_text, NULL))&#xd;\nas `OtherDrugsPrescribed`,&#xd;\nGROUP_CONCAT(IF(obs.concept_id=1271,cn1.name, NULL))&#xd;\nas `LabOrders`,&#xd;\nMAX(IF(obs.concept_id=1271 AND obs.value_coded=657,&apos;Yes&apos;, NULL))&#xd;\nas `CD4Order`,&#xd;\nMAX(IF(obs.concept_id=1271 AND obs.value_coded=856,&apos;Yes&apos;, NULL))&#xd;\n   as `ViralLoadOrder`,&#xd;\nMAX(IF(obs.concept_id=165081,obs.value_text, NULL))&#xd;\nas `OtherTestOrdered`,&#xd;\nMAX(IF(obs.concept_id=164980,cn1.name, NULL))&#xd;\nas `IndicationForViralLoad`,&#xd;\nGROUP_CONCAT(IF(obs.concept_id=1272,cn1.name, NULL))&#xd;\nas `Referrals`,&#xd;\nMAX(IF(obs.concept_id= 165045,obs.value_text, NULL))&#xd;\nas `AdditionalComments`,&#xd;\nMAX(IF(obs.concept_id=5096,DATE_FORMAT(obs.value_datetime,&apos;%d-%b-%Y&apos;), NULL))&#xd;\nas `NextAppointmentDate`&#xd;\n FROM&#xd;\n   obs&#xd;\n   LEFT JOIN patient ON(patient.patient_id=obs.person_id and obs.voided=0 and patient.voided=0)&#xd;\n   LEFT join encounter enc on(enc.encounter_id=obs.encounter_id and enc.voided=0 and obs.voided=0 and enc.form_id=14)&#xd;\n   LEFT JOIN visit on (enc.visit_id=visit.visit_id and visit.voided=0)&#xd;\n   left join concept_name cn1 on(obs.value_coded=cn1.concept_id and cn1.locale=&apos;en&apos; and cn1.locale_preferred=1)&#xd;\n   LEFT JOIN patient_identifier pid1 on(pid1.patient_id=patient.patient_id and patient.voided=0 and pid1.identifier_type=6 and pid1.voided=0)&#xd;\n   LEFT JOIN patient_identifier pid2 on(pid2.patient_id=patient.patient_id and patient.voided=0 and pid2.identifier_type=4 and pid2.voided=0)&#xd;\n   LEFT JOIN patient_identifier pid3 on(pid3.patient_id=patient.patient_id and patient.voided=0 and pid3.identifier_type=5 and pid3.voided=0)&#xd;\n   LEFT JOIN global_property on(global_property.property=&apos;facility_datim_code&apos;)&#xd;\n   WHERE patient.voided=0&#xd;\n   and enc.voided=0 and enc.encounter_datetime BETWEEN :startDate and :endDate&#xd;\n   GROUP BY patient.patient_id, enc.encounter_id;&#xd;\n   &#xd;\n</sqlQuery>\n</org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition>', '2019-07-01 12:38:12', 1, '2020-09-28 05:52:30', 1, 0, NULL, NULL, NULL, '9872d016-30c5-48f4-8115-5590e2a9dc8d')/0xd
INSERT INTO `serialized_object` VALUES ('', 'LAB-ViralLoads', 'Viral load result data entered into the system', 'org.openmrs.module.reporting.dataset.definition.DataSetDefinition', 'org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition', 'org.openmrs.module.reporting.serializer.ReportingSerializer', '<org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition id=\"1\" uuid=\"1253836d-1920-4a67-84b1-9cabe146c33f\" retired=\"false\">\n  <name>LAB-ViralLoads</name>\n  <description>Viral load result data entered into the system</description>\n  <creator id=\"2\" uuid=\"1c3db49d-440a-11e6-a65c-00e04c680037\"/>\n  <dateCreated id=\"3\">2019-07-01 12:04:25 UTC</dateCreated>\n  <changedBy reference=\"2\"/>\n  <dateChanged id=\"4\">2020-09-28 11:30:54 UTC</dateChanged>\n  <parameters id=\"5\">\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"6\">\n      <name>startDate</name>\n      <label>Start Date</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"7\">\n      <name>endDate</name>\n      <label>End Date</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n  </parameters>\n  <id>34</id>\n  <sqlQuery>select&#xd;\nglobal_property.property_value as DatimCode,&#xd;\npid1.identifier as PepfarID,&#xd;\npid2.identifier as HospID,&#xd;\npid3.identifier as ANCNo,&#xd;\npid4.identifier as HTSID,&#xd;\npid5.identifier as HEINo,&#xd;\nperson.gender as Sex,&#xd;\nperson.birthdate as DoB,&#xd;\nTIMESTAMPDIFF(YEAR,person.birthdate,curdate()) as Age,&#xd;\nDATE_FORMAT(encounter.encounter_datetime,&apos;%d-%b-%Y&apos;) as VisitDateSameAsSampleCollectionDate,&#xd;\nform.name as PMMForm,&#xd;\nCONCAT(pn2.given_name,&apos;&apos;,pn2.family_name) as Provider,&#xd;\nMAX(IF(obs.concept_id=856,obs.value_numeric, NULL)) AS ViralLoad,&#xd;\nMAX(IF(obs.concept_id=164989,obs.value_text, NULL)) AS OrderedBy,&#xd;\nMAX(IF(obs.concept_id=164983,obs.value_text, NULL)) AS CheckedBy,&#xd;\nMAX(IF(obs.concept_id=164982,obs.value_text, NULL)) AS ReportedBy,&#xd;\nMAX(IF(obs.concept_id=164989,obs.value_datetime, NULL)) AS OrderedDate,&#xd;\nMAX(IF(obs.concept_id=159951,obs.value_datetime, NULL)) AS SampleCollectionDate,&#xd;\nMAX(IF(obs.concept_id=164984,obs.value_datetime, NULL)) AS CheckedDate,&#xd;\nMAX(IF(obs.concept_id=165414,obs.value_datetime, NULL)) AS ReportedDate&#xd;\nFROM&#xd;\nobs&#xd;\ninner join patient on(patient.patient_id=obs.person_id and patient.voided=0)&#xd;\nleft join patient_identifier pid1 on(pid1.patient_id=obs.person_id and pid1.identifier_type=4 and pid1.voided=0)&#xd;\nleft join patient_identifier pid2 on(pid2.patient_id=obs.person_id and pid2.identifier_type=5 and pid2.voided=0)&#xd;\n&#xd;\nleft join patient_identifier pid3 on(pid3.patient_id=patient.patient_id and pid3.identifier_type=6 and pid3.voided=0)&#xd;\n	&#xd;\nleft join patient_identifier pid4 on(pid4.patient_id=patient.patient_id and pid4.identifier_type=8 and pid4.voided=0)&#xd;\n	&#xd;\nleft join patient_identifier pid5 on(pid5.patient_id=patient.patient_id and pid5.identifier_type=7 and pid5.voided=0)&#xd;\n	&#xd;\nleft join encounter on(encounter.encounter_id=obs.encounter_id and encounter.voided=0)&#xd;\nleft join encounter_provider on(encounter_provider.encounter_id=encounter.encounter_id and encounter.voided=0)&#xd;\nleft join users usr1 on(usr1.user_id=encounter.creator and encounter.voided=0)&#xd;\nleft join person_name pn1 on(usr1.person_id=pn1.person_id and pn1.voided=0)&#xd;\nleft join form on(form.form_id=encounter.form_id and encounter.voided=0)&#xd;\nleft join concept_name cn1 on(obs.value_coded=cn1.concept_id and cn1.locale=&apos;en&apos; and cn1.locale_preferred=1)&#xd;\nleft join person_name pn2 on(pn2.person_id=encounter_provider.provider_id)&#xd;\nleft join person on(person.person_id=obs.person_id and person.voided=0)&#xd;\nLEFT JOIN global_property on(global_property.property=&apos;facility_datim_code&apos;)&#xd;\nwhere &#xd;\nencounter.form_id =21&#xd;\n and&#xd;\nencounter.encounter_datetime BETWEEN :startDate and :endDate&#xd;\nand&#xd;\nobs.concept_id in (856,164989,164983,164982,164989,164984,165414,159951)&#xd;\nGROUP BY encounter.patient_id,obs.encounter_id;</sqlQuery>\n</org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition>', '2019-07-01 13:04:25', 1, '2020-09-28 12:30:54', 1, 0, NULL, NULL, NULL, '1253836d-1920-4a67-84b1-9cabe146c33f')/0xd
INSERT INTO `serialized_object` VALUES ('', 'SI-RADET', 'Export of the RADET dataset', 'org.openmrs.module.reporting.dataset.definition.DataSetDefinition', 'org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition', 'org.openmrs.module.reporting.serializer.ReportingSerializer', '<org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition id=\"1\" uuid=\"6462dd79-d0bf-4686-97d1-fa29ad2ae03d\" retired=\"false\">\n  <name>SI-RADET</name>\n  <description>Export of the RADET dataset</description>\n  <creator id=\"2\" uuid=\"1c3db49d-440a-11e6-a65c-00e04c680037\"/>\n  <dateCreated id=\"3\">2019-07-08 23:21:26 UTC</dateCreated>\n  <changedBy reference=\"2\"/>\n  <dateChanged id=\"4\">2020-11-16 00:05:31 UTC</dateChanged>\n  <parameters id=\"5\">\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"6\">\n      <name>startDate</name>\n      <label>Start Date</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"7\">\n      <name>endDate</name>\n      <label>End Date</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n  </parameters>\n  <id>35</id>\n  <sqlQuery>select &#xd;\nglobal_property.property_value as DatimCode,&#xd;\npid1.identifier as `PatientUniqueID`,&#xd;\npid2.identifier as  `PatientHospitalNo`,&#xd;\npid3.identifier as  `ANCNo`,&#xd;\npid4.identifier as  `HTSNo`,&#xd;\nperson.gender as `Sex`,&#xd;\nMAX(IF(obs.concept_id=159599,IF(TIMESTAMPDIFF(YEAR,person.birthdate,obs.value_datetime)&gt;=5,TIMESTAMPDIFF(YEAR,person.birthdate,obs.value_datetime),@ageAtStart:=0),null)) as  `AgeAtStartOfARTYears`,&#xd;\nMAX(IF(obs.concept_id=159599,IF(TIMESTAMPDIFF(YEAR,person.birthdate,obs.value_datetime)&lt;5,TIMESTAMPDIFF(MONTH,person.birthdate,obs.value_datetime),null),null)) as `AgeAtStartOfARTMonths`,&#xd;\n&#xd;\nMAX(IF(obs.concept_id=160540,cn1.name,null)) as CareEntryPoint,&#xd;\n&#xd;\nMAX(IF(obs.concept_id=165242,cn1.name,null)) as TransferInStatus,&#xd;\nMAX(IF(obs.concept_id=159599,DATE_FORMAT(obs.value_datetime,&apos;%d-%b-%Y&apos;),null)) as `ARTStartDate`,&#xd;\nMAX(IF((obs.concept_id=165708 and enc.form_id=27),DATE_FORMAT(sinner.last_date,&apos;%d-%b-%Y&apos;),null)) as `LastPickupDate`,&#xd;\nenc_last.last_visit_date as LastVisitDate,&#xd;\ngetconceptval(MAX(IF(obs.concept_id=162240,obs.obs_id,null) ),159368,patient.patient_id) as `DaysOfARVRefil`,&#xd;\nMAX(IF(obs2.concept_id=165708,cn2.name,null)) as `InitialRegimenLine`,&#xd;\n&#xd;\nMAX(IF(obs2.concept_id=165708,getcurrentregimen(obs2.value_coded,obs2.encounter_id),null)) as `InitialRegimen`,&#xd;\n&#xd;\nMAX(IF((obs.concept_id=165708 and enc.form_id=27),cn1.name,null)) as `CurrentRegimenLine`,&#xd;\n&#xd;\nMAX(IF((obs.concept_id=165708 and enc.form_id=27),getcurrentregimen(obs.value_coded,obs.encounter_id),null)) as `CurrentRegimen`,&#xd;\n&#xd;\nMAX(IF((obs.concept_id=165050 AND person.gender=&apos;F&apos;),cn1.name,null)) as `PregnancyStatus`,&#xd;\nMAX(IF((obs.concept_id=165050 AND person.gender=&apos;F&apos;),DATE_FORMAT(obs.obs_datetime,&apos;%d-%b-%Y&apos;),null)) as `PregnancyStatusDate`,&#xd;\nMAX(IF((obs.concept_id=5596 AND person.gender=&apos;F&apos;),DATE_FORMAT(obs.value_datetime,&apos;%d-%b-%Y&apos;),null)) as `EDD`,&#xd;\n&#xd;\nMAX(IF((obs.concept_id=1427 AND person.gender=&apos;F&apos;),DATE_FORMAT(obs.value_datetime,&apos;%d-%b-%Y&apos;),null)) as `LMP`,&#xd;\n&#xd;\nTIMESTAMPDIFF(&#xd;\nWEEK,&#xd;\nMAX(IF((obs.concept_id=1427 AND person.gender=&apos;F&apos;),obs.value_datetime,null)),&#xd;\n:endDate) as `EstimatedGestationAgeWeeks`,&#xd;\nMAX(IF(obs.concept_id=856 AND enc.form_id=21,obs.value_numeric,null)) as `CurrentViralLoad(c/ml)`,&#xd;\nMAX(IF(obs.concept_id=856 AND enc.form_id=21,DATE_FORMAT(obs.obs_datetime,&apos;%d-%b-%Y&apos;),NULL)) as `ViralLoadSampleCollectionDate`,&#xd;\nIF(&#xd;\nMAX(IF(obs.concept_id=856 AND enc.form_id=21,obs.value_numeric,NULL)) is not null,&#xd;\nDATE_FORMAT(getreporteddate(obs.encounter_id,165414,obs.person_id),&apos;%d-%b-%Y&apos;),&#xd;\nnull&#xd;\n) as `ViralLoadReportedDate`,&#xd;\n&#xd;\nIF(&#xd;\nMAX(IF(obs.concept_id=856 AND enc.form_id=21,obs.value_numeric,NULL)) is not null,&#xd;\nMAX(IF(obs.concept_id=164980,cn1.name,null)),&#xd;\nnull&#xd;\n&#xd;\n) as `ViralLoadIndication`,&#xd;\n&#xd;\nIFNULL(MAX(IF(obs.concept_id=165470,cn1.name,null)) ,&#xd;\ngetoutcome(&#xd;\nMAX(IF((obs.concept_id=165708 and enc.form_id=27),sinner.last_date,null)),&#xd;\ngetconceptval(MAX(IF(obs.concept_id=162240,obs.obs_id,null) ),159368,patient.patient_id) ,&#xd;\n28,&#xd;\nIF(:endDate IS NULL or :endDate = &apos;&apos;, CURDATE(),:endDate)&#xd;\n) ) as `CurrentARTStatus`,&#xd;\n&#xd;\nMAX(IF(obs.concept_id=165775,DATE_FORMAT(obs.value_datetime,&apos;%d-%b-%Y&apos;),null)) as `DateReturnedToCare`,&#xd;\n&#xd;\nMAX(IF(obs.concept_id=165469,DATE_FORMAT(obs.value_datetime,&apos;%d-%b-%Y&apos;),null)) as `DateOfTermination`,&#xd;\n&#xd;\nMAX(IF(obs.concept_id=5096,DATE_FORMAT(obs.value_datetime,&apos;%d-%b-%Y&apos;),null)) as `LastAppointmentDate`,&#xd;\n&#xd;\nIF(TIMESTAMPDIFF(YEAR,person.birthdate,curdate())&gt;=5,TIMESTAMPDIFF(YEAR,person.birthdate,curdate()),null) as `CurrentAgeYears`,&#xd;\nIF(TIMESTAMPDIFF(YEAR,person.birthdate,curdate())&lt;5,TIMESTAMPDIFF(MONTH,person.birthdate,curdate()),null) as `CurrentAgeMonths`,&#xd;\nDATE_FORMAT(person.birthdate,&apos;%d-%b-%Y&apos;) as `DateOfBirth`,&#xd;\nIF(person.dead=1,&quot;Dead&quot;,&quot;&quot;) as MarkAsDeseased,&#xd;\nIF(person.dead=1,person.death_date,&quot;&quot;) as MarkAsDeseasedDeathDate,&#xd;\nCONCAT(&quot;+234-&quot;,psn_atr.value) AS RegistrationPhoneNo,&#xd;\nMAX(IF(obs.concept_id=159635,CONCAT(&quot;+234-&quot;,obs.value_text),null)) as `ContactPhoneNo`,&#xd;\nIF(biometrictable.patient_Id IS NOT NULL,&apos;Yes&apos;,&apos;No&apos;) as BiometricCaptured,&#xd;\nIF(biometrictable.patient_Id IS NOT NULL,IF(invalidprint.patient_Id IS NOT NULL,&apos;No&apos;,&apos;&apos;),&quot;&quot;) as ValidCapture,&#xd;\nMAX(IF(obs.concept_id=5089,obs.value_numeric,null)) as `CurrentWeight(Kg)`,&#xd;\nMAX(IF(obs.concept_id=5089,DATE_FORMAT(obs.obs_datetime,&apos;%d-%b-%Y&apos;),null)) as `CurrentWeightDate`,&#xd;\nMAX(IF(obs.concept_id=1659,cn1.name,null)) as `TBStatus`,&#xd;\nMAX(IF(obs.concept_id=1659,DATE_FORMAT(obs.obs_datetime,&apos;%d-%b-%Y&apos;),null)) as `TBStatusDate`,&#xd;\nMAX(IF(obs.concept_id=164852,DATE_FORMAT(obs.value_datetime,&apos;%d-%b-%Y&apos;),null)) as `INHStartDate`,&#xd;\nMAX(IF(obs.concept_id=166096,DATE_FORMAT(obs.value_datetime,&apos;%d-%b-%Y&apos;),null)) as `INHStopDate`,&#xd;\nMAX(IF(obs.concept_id=165727 AND obs.value_coded=1679,obs.obs_datetime,null)) as LastINHDispensedDate,&#xd;\nMAX(IF(obs.concept_id=1113,DATE_FORMAT(obs.value_datetime,&apos;%d-%b-%Y&apos;),null)) as `TBTreatmentStartDate`,&#xd;\nMAX(IF(obs.concept_id=159431,DATE_FORMAT(obs.value_datetime,&apos;%d-%b-%Y&apos;),null)) as `TBTreatmentStopDate`,&#xd;\nMAX(IF(enc.form_id=67, DATE_FORMAT(@lastSampleDate:=enc.encounter_datetime,&apos;%d-%b-%Y&apos;),NULL)) as `LastViralLoadSampleCollectionFormDate`,&#xd;\nMAX(IF(obs.concept_id=166156,DATE_FORMAT(obs.value_datetime,&apos;%d-%b-%Y&apos;),null)) as `OTZStartDate`,&#xd;\nMAX(IF(obs.concept_id=166158,DATE_FORMAT(obs.value_datetime,&apos;%d-%b-%Y&apos;),null)) as `OTZStopDate`,&#xd;\nDATE_FORMAT(pprg.date_enrolled,&apos;%d-%b-%Y&apos;) as EnrollmentDate,&#xd;\nMAX(IF(obs2.concept_id in(164506,164507), cn2.name,null)) as `InitialFirstLineRegimen`,&#xd;\nMAX(IF(obs2.concept_id in(164506,164507), DATE_FORMAT(obs2.obs_datetime,&apos;%d-%b-%Y&apos;),null)) as `InitialFirstLineRegimenDate`,&#xd;\nMAX(IF(obs2.concept_id in(164513,164514), cn2.name,null)) as `InitialSecondLineRegimen`,&#xd;\nMAX(IF(obs2.concept_id in(164513,164514), DATE_FORMAT(obs2.obs_datetime,&apos;%d-%b-%Y&apos;),null)) as `InitialSecondLineRegimenDate`,&#xd;\nMAX(IF((obs.concept_id in(164506,164507) and enc.form_id=27), cn1.name,null)) as `LastFirstLineRegimen`,&#xd;\nMAX(IF((obs.concept_id in(164506,164507) and enc.form_id=27), DATE_FORMAT(obs2.obs_datetime,&apos;%d-%b-%Y&apos;),null)) as `LastFirstLineRegimenDate`,&#xd;\nMAX(IF((obs.concept_id in(164513,164514) and enc.form_id=27), cn1.name,null)) as `LastSecondLineRegimen`,&#xd;\nMAX(IF((obs.concept_id in(164513,164514) and enc.form_id=27), DATE_FORMAT(obs2.obs_datetime,&apos;%d-%b-%Y&apos;),null)) as `LastSecondLineRegimenDate`&#xd;\n  from patient&#xd;\n  INNER JOIN person on(person.person_id=patient.patient_id and patient.voided=0)&#xd;\n  LEFT JOIN patient_identifier pid1 on(pid1.patient_id=patient.patient_id and patient.voided=0 and pid1.identifier_type=4 and pid1.voided=0)&#xd;\n  &#xd;\n  LEFT JOIN patient_identifier pid3 on(pid3.patient_id=patient.patient_id and patient.voided=0 and pid3.identifier_type=6 and pid3.voided=0)&#xd;\n  &#xd;\n  LEFT JOIN patient_identifier pid4 on(pid4.patient_id=patient.patient_id and patient.voided=0 and pid4.identifier_type=8 and pid4.voided=0)&#xd;\n  &#xd;\n  LEFT JOIN person_attribute psn_atr ON (person.person_id=psn_atr.person_id and psn_atr.person_attribute_type_id=8) &#xd;\n  LEFT JOIN patient_program pprg on(pprg.patient_id=patient.patient_id and pprg.voided=0 and patient.voided=0 and pprg.program_id=1)&#xd;\n  LEFT JOIN patient_identifier pid2 on(pid2.patient_id=patient.patient_id and patient.voided=0 and pid2.identifier_type=5 and pid2.voided=0)&#xd;\n  LEFT JOIN&#xd;\n  (select &#xd;\nobs.person_id,&#xd;\nobs.concept_id,&#xd;\n MAX(obs.obs_datetime) as last_date, &#xd;\nMIN(obs.obs_datetime) as first_date&#xd;\nfrom obs where obs.voided=0 and obs.obs_datetime&lt;=:endDate and concept_id in(159599,165708,159368,164506,164513,164507,164514,165702,165703,165050,&#xd;\n856,164980,165470,159635,5089,165988,1659,164852,166096,1113,159431,162240,165242,165724,166156,166158,165727,164982,165414,5596,165775,160540,165469,5096,1427) GROUP BY obs.person_id, obs.concept_id) as sinner on (sinner.person_id=patient.patient_id and patient.voided=0)&#xd;\nINNER JOIN obs on(obs.person_id=patient.patient_id and obs.concept_id=sinner.concept_id and obs.obs_datetime=sinner.last_date and obs.voided=0 and obs.obs_datetime&lt;=:endDate)&#xd;\nINNER JOIN obs obs2 on(obs2.person_id=patient.patient_id and obs2.concept_id=sinner.concept_id and obs2.obs_datetime=sinner.first_date and obs2.voided=0 and obs2.obs_datetime&lt;=:endDate)&#xd;\nLEFT join encounter enc on(enc.encounter_id=obs.encounter_id and enc.voided=0 and obs.voided=0)&#xd;\nleft join concept_name cn1 on(obs.value_coded=cn1.concept_id and cn1.locale=&apos;en&apos; and cn1.locale_preferred=1)&#xd;\nleft join concept_name cn2 on(obs2.value_coded=cn2.concept_id and cn2.locale=&apos;en&apos; and cn2.locale_preferred=1)&#xd;\nLEFT JOIN (&#xd;\n   select &#xd;\n   DISTINCT biometricinfo.patient_Id&#xd;\n   from &#xd;\n   biometricinfo&#xd;\n) as biometrictable &#xd;\non(patient.patient_id=biometrictable.patient_Id and patient.voided=0)&#xd;\nLEFT JOIN (&#xd;\n   select &#xd;\n   DISTINCT biometricinfo.patient_Id&#xd;\n   from &#xd;\n   biometricinfo where template not like &apos;Rk1S%&apos;&#xd;\n) as invalidprint &#xd;\non(patient.patient_id=invalidprint.patient_Id and patient.voided=0)&#xd;\nLEFT JOIN global_property on(global_property.property=&apos;facility_datim_code&apos;)&#xd;\nLEFT JOIN (&#xd;\nselect &#xd;\nencounter.patient_id,&#xd;\nMAX(encounter.encounter_datetime) as last_visit_date&#xd;\nfrom encounter&#xd;\nwhere encounter.voided=0 and encounter.form_id!=13 and encounter.encounter_datetime&lt;=:endDate GROUP BY  encounter.patient_id&#xd;\n) as enc_last on(enc_last.patient_id=patient.patient_id and patient.voided=0)&#xd;\nWHERE patient.voided=0 and pprg.program_id=1&#xd;\nGROUP BY patient.patient_id ;&#xd;\n</sqlQuery>\n</org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition>