INSERT INTO `serialized_object` VALUES ('', 'Tx_NEW', '', 'org.openmrs.module.reporting.dataset.definition.DataSetDefinition', 'org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition', 'org.openmrs.module.reporting.serializer.ReportingSerializer', '<org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition id=\"1\" uuid=\"ec139e62-7be9-4fab-8222-86914985f491\" retired=\"false\">\n  <name>Tx_NEW</name>\n  <description></description>\n  <creator id=\"2\" uuid=\"1c3db49d-440a-11e6-a65c-00e04c680037\"/>\n  <dateCreated id=\"3\">2019-09-02 14:25:42 UTC</dateCreated>\n  <changedBy reference=\"2\"/>\n  <dateChanged id=\"4\">2020-09-28 11:22:27 UTC</dateChanged>\n  <parameters id=\"5\">\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"6\">\n      <name>startDate</name>\n      <label>Start Date</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"7\">\n      <name>endDate</name>\n      <label>End Date</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n  </parameters>\n  <id>47</id>\n  <sqlQuery>select&#xd;\n    DISTINCT&#xd;\n	global_property.property_value as DatimCode,&#xd;\n        pid1.identifier as HospID,&#xd;\n	pid2.identifier as PepfarID,&#xd;\n	pid3.identifier as ANCNo,&#xd;\n	pid4.identifier as HTSID,&#xd;\n	pid5.identifier as HEINo,&#xd;\n	person.gender as Sex,&#xd;\n	person.birthdate as DOB,&#xd;\n	TIMESTAMPDIFF(YEAR,person.birthdate,curdate()) as Age,&#xd;\n	patient_program.date_enrolled as EnrollmentDate,&#xd;\n	MAX(IF(obs.concept_id=160540,cn1.name,null)) as CareEntryPoint,&#xd;\n	MAX(IF(obs.concept_id=159599,DATE_FORMAT(obs.value_datetime,&apos;%d-%b-%Y&apos;), NULL)) as ARTStartDate&#xd;\n	from &#xd;\n	patient &#xd;\n	left join patient_identifier pid1 on(pid1.patient_id=patient.patient_id and pid1.identifier_type=5 and pid1.voided=0)&#xd;\n	&#xd;\n	left join patient_identifier pid2 on(pid2.patient_id=patient.patient_id and pid2.identifier_type=4 and pid2.voided=0)&#xd;\n	&#xd;\n	left join patient_identifier pid3 on(pid3.patient_id=patient.patient_id and pid3.identifier_type=6 and pid3.voided=0)&#xd;\n	&#xd;\n	left join patient_identifier pid4 on(pid4.patient_id=patient.patient_id and pid4.identifier_type=8 and pid4.voided=0)&#xd;\n	&#xd;\n	left join patient_identifier pid5 on(pid5.patient_id=patient.patient_id and pid5.identifier_type=7 and pid5.voided=0)&#xd;\n	&#xd;\n	left join person on(person.person_id=patient.patient_id and person.voided=0)&#xd;\n	&#xd;\n	left join patient_program on(patient_program.patient_id=patient.patient_id and patient_program.program_id=1 and patient_program.voided=0)&#xd;\n	LEFT JOIN encounter enc on(enc.patient_id=patient.patient_id and enc.form_id IN(23,56) and enc.voided=0)&#xd;\n	LEFT JOIN obs on(enc.encounter_id=obs.encounter_id and obs.voided=0)&#xd;\n	left join concept_name cn1 on(obs.value_coded=cn1.concept_id and cn1.locale=&apos;en&apos; and cn1.locale_preferred=1)&#xd;\n	LEFT JOIN global_property on(global_property.property=&apos;facility_datim_code&apos;)&#xd;\n	&#xd;\n	&#xd;\n	where patient.voided=0 and enc.voided=0&#xd;\n	GROUP BY patient.patient_id&#xd;\n	HAVING&#xd;\n    MAX(IF(obs.concept_id=159599,obs.value_datetime, NULL)) BETWEEN :startDate and :endDate;&#xd;\n	</sqlQuery>\n</org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition>', '2019-09-02 15:25:42', 1, '2020-09-28 12:22:27', 1, 0, NULL, NULL, NULL, 'ec139e62-7be9-4fab-8222-86914985f491')/0xd
INSERT INTO `serialized_object` VALUES ('', 'Viral Load Eligibility 2.0', '', 'org.openmrs.module.reporting.dataset.definition.DataSetDefinition', 'org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition', 'org.openmrs.module.reporting.serializer.ReportingSerializer', '<org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition id=\"1\" uuid=\"e76833ab-1748-4a03-8f97-2229afb66dc1\" retired=\"false\">\n  <name>Viral Load Eligibility 2.0</name>\n  <description></description>\n  <creator id=\"2\" uuid=\"1c3db49d-440a-11e6-a65c-00e04c680037\"/>\n  <dateCreated id=\"3\">2019-09-02 14:28:56 UTC</dateCreated>\n  <changedBy reference=\"2\"/>\n  <dateChanged id=\"4\">2020-09-02 12:12:33 UTC</dateChanged>\n  <parameters id=\"5\">\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"6\">\n      <name>startDate</name>\n      <label>Start Date</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"7\">\n      <name>endDate</name>\n      <label>End Date</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n  </parameters>\n  <id>49</id>\n  <sqlQuery>SELECT&#xd;\npid1.identifier AS PepfarID,&#xd;\npid2.identifier AS HospID,&#xd;\npn3.given_name AS FirstName,&#xd;\npn3.family_name AS FamilyName,&#xd;\nperson.gender AS Sex,&#xd;\nCONCAT(person_address.address1,&quot; &quot;,person_address.city_village) as PatientAddress,&#xd;\nCONCAT(&quot;234-&quot;,psn_atr.value) AS RegistrationPhoneNo,&#xd;\nMAX(IF(target.concept_id=159635, CONCAT(&quot;234-&quot;,obs.value_text), NULL)) AS `CurrentPhoneNo`,&#xd;\nMAX(IF(target.concept_id=164946, CONCAT(&quot;234-&quot;,obs.value_text), NULL)) AS `NextOfKinPhoneNo`,&#xd;\nDATE_FORMAT (person.birthdate, &apos;%d-%b-%Y&apos;) AS DOB,&#xd;\nTIMESTAMPDIFF(YEAR,person.birthdate,CURDATE()) AS Age,&#xd;\nDATE_FORMAT (pprg.date_enrolled , &apos;%d-%b-%Y&apos;) AS EnrollmentDate,&#xd;\nMAX(IF(target.concept_id=165050 AND person.gender=&apos;F&apos;, cn1.name, NULL)) AS  `PregnancyStatus`,&#xd;\nMAX(IF(target.concept_id=165050 AND person.gender=&apos;F&apos;, DATE_FORMAT(obs.obs_datetime,&apos;%d-%b-%Y&apos;), NULL)) AS  `PregnancyStatusDate`&#xd;\n,MAX(IF(target.concept_id=5596 AND person.gender=&apos;F&apos;, DATE_FORMAT(obs.value_datetime,&apos;%d-%b-%Y&apos;),null)) as `EDD`&#xd;\n,MAX(IF(target.concept_id=159599, DATE_FORMAT(obs.value_datetime,&apos;%d-%b-%Y&apos;), NULL)) AS  `ARTStartDate`&#xd;\n,MAX(IF((target.concept_id=165708 and enc.form_id=27), DATE_FORMAT(target.last_date, &apos;%d-%b-%Y&apos;), NULL)) AS  `LastDrugPickUpDate`&#xd;\n,getconceptval(MAX(IF(obs.concept_id=162240,obs.obs_id,null) ),159368,patient.patient_id) as `DaysOfARVRefil`&#xd;\n&#xd;\n,IFNULL(MAX(IF(obs.concept_id=165470,cn1.name,null)) ,&#xd;\ngetoutcome(&#xd;\nMAX(IF((obs.concept_id=165708 and enc.form_id=27),target.last_date,null)),&#xd;\ngetconceptval(MAX(IF(obs.concept_id=162240,obs.obs_id,null) ),159368,patient.patient_id) ,&#xd;\n28,&#xd;\nIF(:endDate IS NULL or :endDate  = &apos;&apos;, CURDATE(),:endDate )&#xd;\n)) as `CurrentARTStatus`&#xd;\n,MAX(IF(target.concept_id=856 AND enc.form_id=21,obs.value_numeric, NULL)) AS  `CurrentViralLoad`&#xd;\n,MAX(IF(target.concept_id=856 AND enc.form_id=21,DATE_FORMAT(obs.obs_datetime,&apos;%d-%b-%Y&apos;), NULL)) AS  `CurrentViralLoadDate`&#xd;\n,MAX(IF(enc.form_id=67, DATE_FORMAT(enc.encounter_datetime,&apos;%d-%b-%Y&apos;),NULL)) as `LastViralLoadSampleCollectionFormDate`&#xd;\n,MAX(IF(target.concept_id=165708 and enc.form_id=27, cn1.name, NULL)) AS  ` CurrentRegimenLine`&#xd;\n,MAX(IF(target.concept_id=164506 and enc.form_id=27 ,cn1.name, NULL)) AS  `LastFirstLineRegimen`&#xd;\n,MAX(IF(target.concept_id=164506 and enc.form_id=27,DATE_FORMAT(obs.obs_datetime , &apos;%d-%b-%Y&apos;), NULL))  AS `LastFirstLineRegimenDate`&#xd;\n,MAX(IF(target.concept_id=164513 and enc.form_id=27,cn1.name, NULL))  AS  `LastSecondLineRegimen`&#xd;\n,MAX(IF(target.concept_id=164513 and enc.form_id=27, DATE_FORMAT(obs.obs_datetime , &apos;%d-%b-%Y&apos;), NULL)) AS  `LastSecondLineRegimenDate`&#xd;\n,MAX(IF(target.concept_id=164702 and enc.form_id=27,cn1.name, NULL))  AS  `LastThirdLineRegimen`&#xd;\n,MAX(IF(target.concept_id=164702 and enc.form_id=27,obs.obs_datetime, NULL))  AS  `LastThirdLineRegimenDate`&#xd;\n&#xd;\nFROM patient&#xd;\nLEFT JOIN patient_identifier pid1 ON(pid1.patient_id=patient .patient_id AND pid1.identifier_type=4 AND pid1.voided=0)&#xd;\nLEFT JOIN patient_identifier pid2 ON(pid2.patient_id=patient .patient_id AND pid2.identifier_type=5 AND pid2.voided=0)&#xd;\nLEFT JOIN person_address on(person_address.person_id=patient.patient_id and person_address.voided=0)&#xd;\nLEFT JOIN person_name pn3 ON(patient .patient_id=pn3.person_id AND pn3.voided=0)&#xd;\nLEFT JOIN person ON(person.person_id=patient .patient_id)&#xd;\nLEFT JOIN person_attribute psn_atr ON (person.person_id=psn_atr.person_id and psn_atr.person_attribute_type_id=8) &#xd;\nLEFT JOIN patient_program pprg ON(pprg.patient_id=person.person_id AND pprg.voided=0 AND pprg.program_id=1)&#xd;\nLEFT JOIN&#xd;\n(&#xd;\nSELECT obs.person_id, obs.concept_id, MAX(obs.obs_datetime) AS last_date FROM obs where obs.obs_datetime&lt;=:endDate and obs.voided=0 and obs.concept_id IN(159599, 5596,165050,856,165708,164506,164507,164513,164514,165702,165703,165470,164989,7778430,159951,159635,164946,159951,159368,162240,159368,165470,165708) GROUP BY obs.person_id, obs.concept_id)&#xd;\ntarget ON (target.person_id=patient.patient_id)&#xd;\n&#xd;\nINNER JOIN obs ON(obs.person_id=target.person_id AND obs.concept_id=target.concept_id AND obs.obs_datetime=target.last_date AND obs.obs_datetime&lt;=:endDate AND obs.concept_id IN(159599, 5596,165050,856,165708,164506,164507,164513,164514,165702,165703,165470,164989,7778430,159951,159635,164946,159951,159368,162240,159368,165470,165708))&#xd;\nINNER JOIN encounter enc on(enc.encounter_id=obs.encounter_id and enc.voided=0)&#xd;\nLEFT JOIN concept_name cn1 ON(obs.value_coded=cn1.concept_id AND cn1.locale=&apos;en&apos; AND cn1.locale_preferred=1)&#xd;\nwhere patient.voided=0&#xd;\nGROUP BY patient.patient_id&#xd;\nHAVING &#xd;\n&#xd;\nIFNULL(MAX(IF(obs.concept_id=165470,cn1.name,null)) ,&#xd;\ngetoutcome(&#xd;\nMAX(IF((obs.concept_id=165708 and enc.form_id=27),target.last_date,null)),&#xd;\ngetconceptval(MAX(IF(obs.concept_id=162240,obs.obs_id,null) ),159368,patient.patient_id) ,&#xd;\n28,&#xd;\nIF(:endDate IS NULL or :endDate  = &apos;&apos;, CURDATE(),:endDate )&#xd;\n) ) = &apos;Active&apos;&#xd;\n&#xd;\nAND&#xd;\nTIMESTAMPDIFF(MONTH,MAX(IF(target.concept_id=159599,obs.obs_datetime, NULL)),:endDate)&gt;=6&#xd;\nAND&#xd;\n(&#xd;\n(&#xd;\nTIMESTAMPDIFF(MONTH,MAX(IF(target.concept_id=856,target.last_date, NULL)),:endDate)&gt;=12&#xd;\nAND &#xd;\nMAX(IF(target.concept_id=856,obs.value_numeric, NULL))&lt;1000&#xd;\n)&#xd;\nOR&#xd;\n(&#xd;\nTIMESTAMPDIFF(MONTH,MAX(IF(target.concept_id=856,target.last_date, NULL)),:endDate)&gt;=3&#xd;\nAND &#xd;\nMAX(IF(target.concept_id=856,obs.value_numeric, NULL))&gt;1000&#xd;\n)&#xd;\nOR&#xd;\nMAX(IF(target.concept_id=856,obs.value_numeric, NULL)) IS NULL&#xd;\n)&#xd;\n</sqlQuery>\n</org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition>', '2019-09-02 15:28:56', 1, '2020-09-02 13:12:33', 1, 0, NULL, NULL, NULL, 'e76833ab-1748-4a03-8f97-2229afb66dc1')/0xd
INSERT INTO `serialized_object` VALUES ('', 'Viral Load Eligibility 2.0', 'A patient is eligible for viral load if;\r\n\r\nPatient has been on ART for more than 6 months,\r\n\r\nPatient is Active on Treatment,\r\n\r\nPatient is supressed and has not had viral load in the past 1 year\r\n\r\nPatient is unsupressed and has not had viral load in the past 3 months\r\n\r\nPatien\'s sample has not been taken\r\n', 'org.openmrs.module.reporting.report.definition.ReportDefinition', 'org.openmrs.module.reporting.report.definition.ReportDefinition', 'org.openmrs.module.reporting.serializer.ReportingSerializer', '<org.openmrs.module.reporting.report.definition.ReportDefinition id=\"1\" uuid=\"9d363fa4-e132-4a39-962f-c9872e285172\" retired=\"false\">\n  <name>Viral Load Eligibility 2.0</name>\n  <description>A patient is eligible for viral load if;&#xd;\n&#xd;\nPatient has been on ART for more than 6 months,&#xd;\n&#xd;\nPatient is Active on Treatment,&#xd;\n&#xd;\nPatient is supressed and has not had viral load in the past 1 year&#xd;\n&#xd;\nPatient is unsupressed and has not had viral load in the past 3 months&#xd;\n&#xd;\nPatien&apos;s sample has not been taken&#xd;\n</description>\n  <creator id=\"2\" uuid=\"1c3db49d-440a-11e6-a65c-00e04c680037\"/>\n  <dateCreated id=\"3\">2019-09-02 14:29:22 UTC</dateCreated>\n  <changedBy reference=\"2\"/>\n  <dateChanged id=\"4\">2020-09-02 12:13:16 UTC</dateChanged>\n  <parameters id=\"5\">\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"6\">\n      <name>startDate</name>\n      <label>Start Date</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"7\">\n      <name>endDate</name>\n      <label>End Date</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n  </parameters>\n  <id>50</id>\n  <dataSetDefinitions class=\"linked-hash-map\" id=\"8\">\n    <entry>\n      <string>Viral-Load-Eligibility-2.0</string>\n      <org.openmrs.module.reporting.evaluation.parameter.Mapped id=\"9\">\n        <parameterizable class=\"org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition\" id=\"10\" uuid=\"e76833ab-1748-4a03-8f97-2229afb66dc1\"/>\n        <parameterMappings id=\"11\">\n          <entry>\n            <string>endDate</string>\n            <string>${endDate}</string>\n          </entry>\n          <entry>\n            <string>startDate</string>\n            <string>${startDate}</string>\n          </entry>\n        </parameterMappings>\n      </org.openmrs.module.reporting.evaluation.parameter.Mapped>\n    </entry>\n  </dataSetDefinitions>\n</org.openmrs.module.reporting.report.definition.ReportDefinition>', '2019-09-02 15:29:22', 1, '2020-09-02 13:13:16', 1, 0, NULL, NULL, NULL, '9d363fa4-e132-4a39-962f-c9872e285172')/0xd
INSERT INTO `serialized_object` VALUES ('', 'MissedAppointment', 'Dataset of patients who missed their scheduled appointments', 'org.openmrs.module.reporting.dataset.definition.DataSetDefinition', 'org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition', 'org.openmrs.module.reporting.serializer.ReportingSerializer', '<org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition id=\"1\" uuid=\"bacb0314-0556-4c62-8f30-999d563938d7\" retired=\"false\">\n  <name>MissedAppointment</name>\n  <description>Dataset of patients who missed their scheduled appointments</description>\n  <creator id=\"2\" uuid=\"1c3db49d-440a-11e6-a65c-00e04c680037\"/>\n  <dateCreated id=\"3\">2019-10-19 15:22:36 UTC</dateCreated>\n  <changedBy reference=\"2\"/>\n  <dateChanged id=\"4\">2020-02-04 12:12:30 UTC</dateChanged>\n  <parameters id=\"5\">\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"6\">\n      <name>startDate</name>\n      <label>startDate</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"7\">\n      <name>endDate</name>\n      <label>endDate</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n  </parameters>\n  <id>215</id>\n  <sqlQuery>select&#xd;\npid1.identifier as PepfarID,&#xd;\npid2.identifier as HospID,&#xd;\npn3.given_name as FirstName,&#xd;\npn3.family_name as FamilyName,&#xd;\nperson.gender as Sex,&#xd;\npsn_atr.value AS PhoneNo,&#xd;\nperson.birthdate as DOB,&#xd;\nCONCAT(person_address.address1,&quot; &quot;,person_address.city_village) as PatientAddress,&#xd;\nTIMESTAMPDIFF(YEAR,person.birthdate,CURDATE()) as Age,&#xd;\nappointmentquery.appointmentdate as AppointmentDate,&#xd;\nvisitquery.visitdate as VisitDate&#xd;\nfrom patient&#xd;\nleft join patient_identifier pid1 on(pid1.patient_id=patient.patient_id and pid1.identifier_type=4 and pid1.voided=0)&#xd;\nleft join patient_identifier pid2 on(pid2.patient_id=patient.patient_id and pid2.identifier_type=5 and pid2.voided=0)&#xd;\nleft join person_name pn3 on(patient.patient_id=pn3.person_id and pn3.voided=0)&#xd;\nleft join person on(person.person_id=patient.patient_id and person.voided=0)&#xd;\nleft join person_address on(person_address.person_id=person.person_id and person_address.voided=0)&#xd;\nLEFT JOIN person_attribute psn_atr ON (person.person_id=psn_atr.person_id and psn_atr.person_attribute_type_id=8) &#xd;\nleft join&#xd;\n(&#xd;\n    select &#xd;\n	   DISTINCT &#xd;\n	   obs.person_id,&#xd;\n	   obs.concept_id,&#xd;\n	   obs.value_datetime as appointmentdate&#xd;\n	   from &#xd;\n	obs where obs.concept_id=5096 and obs.voided=0 and obs.value_datetime BETWEEN :startDate and :endDate GROUP BY obs.person_id,obs.concept_id,obs.value_datetime&#xd;\n	   &#xd;\n) as appointmentquery on(appointmentquery.person_id=patient.patient_id)&#xd;\nleft join &#xd;\n(&#xd;\n    select &#xd;\n	  DISTINCT &#xd;\n	  encounter.patient_id,&#xd;\n	  encounter.encounter_datetime as visitdate&#xd;\n    from encounter &#xd;\n	  where &#xd;\n	  encounter.voided=0 &#xd;\n	  and &#xd;\n	  encounter.encounter_datetime BETWEEN :startDate and :endDate&#xd;\n	  GROUP BY encounter.patient_id,encounter.encounter_datetime&#xd;\n	  &#xd;\n) as visitquery on(visitquery.patient_id=appointmentquery.person_id)&#xd;\nwhere patient.voided=0 &#xd;\nand appointmentquery.appointmentdate BETWEEN :startDate  and :endDate&#xd;\nAND &#xd;\nvisitquery.visitdate is null&#xd;\nGROUP BY patient.patient_id</sqlQuery>\n</org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition>', '2019-10-19 16:22:36', 1, '2020-02-04 13:12:30', 1, 0, NULL, NULL, NULL, 'bacb0314-0556-4c62-8f30-999d563938d7')/0xd
INSERT INTO `serialized_object` VALUES ('', 'EXITS', 'Patients who had contact tracking and termination form filled for them', 'org.openmrs.module.reporting.dataset.definition.DataSetDefinition', 'org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition', 'org.openmrs.module.reporting.serializer.ReportingSerializer', '<org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition id=\"1\" uuid=\"a021b9b8-179a-4d62-9fbb-22181a8bad5e\" retired=\"false\">\n  <name>EXITS</name>\n  <description>Patients who had contact tracking and termination form filled for them</description>\n  <creator id=\"2\" uuid=\"1c3db49d-440a-11e6-a65c-00e04c680037\"/>\n  <dateCreated id=\"3\">2019-10-24 02:20:20 UTC</dateCreated>\n  <changedBy reference=\"2\"/>\n  <dateChanged id=\"4\">2020-09-28 10:52:40 UTC</dateChanged>\n  <parameters id=\"5\">\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"6\">\n      <name>startDate</name>\n      <label>startDate</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"7\">\n      <name>endDate</name>\n      <label>endDate</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n  </parameters>\n  <id>217</id>\n  <sqlQuery>select  &#xd;\n   global_property.property_value as DatimCode,&#xd;\n   pid1.identifier as PepfarID,&#xd;\n   pid2.identifier as HospID,&#xd;\n   person.gender as Sex,&#xd;\n   TIMESTAMPDIFF(YEAR,person.birthdate,curdate()) as Age,&#xd;\n   person.birthdate as DOB,&#xd;\n   IF(person.dead=1,&quot;Dead&quot;,&quot;&quot;) as MarkAsDeseased,&#xd;\n   IF(person.dead=1,person.death_date,&quot;&quot;) as MarkAsDeseasedDeathDate,&#xd;\n   encounter.encounter_datetime as TrackorTerminationDate,&#xd;\n   form.`name` as PmmForm,&#xd;\n   CONCAT(prs1.given_name,&apos; &apos;,prs1.family_name) as EnteredBy,&#xd;\n   encounter.date_created as DateCreated,&#xd;\n   MAX(IF(obs.concept_id=165460, cn1.name, NULL)) as  `ReasonForTracking`&#xd;\n   ,MAX(IF(obs.concept_id=165586, cn1.name, NULL)) as `PatientCareTerminated`&#xd;\n   ,MAX(IF(obs.concept_id=165469, obs.value_datetime, NULL)) as  `DateOfTermination`&#xd;\n   ,MAX(IF(obs.concept_id=165470, cn1.name, NULL)) as  `ReasonForTermination`&#xd;\n   ,MAX(IF(obs.concept_id=165889, cn1.name, NULL)) as  `CauseOfDeath`&#xd;\n   ,MAX(IF(obs.concept_id=165915, obs.value_text, NULL)) as  `OtherCauseOfDeath`&#xd;\n   ,MAX(IF(obs.concept_id=165916, obs.value_text, NULL)) as  `DiscontinuedCareReason`&#xd;\n   ,MAX(IF(obs.concept_id=165775, obs.value_datetime, NULL)) as  `DateReturnedToCare`&#xd;\n   ,MAX(IF(obs.concept_id=165776, cn1.name, NULL)) as  `ReferredFor`&#xd;\n      &#xd;\n  FROM encounter &#xd;\n  left join patient on(encounter.patient_id=patient.patient_id and patient.voided=0 and encounter.voided=0)&#xd;\n  left join person_attribute on(person_attribute.person_id=patient.patient_id and person_attribute.voided=0 and&#xd;\n  person_attribute.person_attribute_type_id=8 and person_attribute.voided=0)&#xd;\n  left join obs on(obs.encounter_id=encounter.encounter_id)&#xd;\n  left join patient_identifier pid1 on(pid1.patient_id=encounter.patient_id and pid1.identifier_type=4)&#xd;\n  left join patient_identifier pid2 on(pid2.patient_id=encounter.patient_id and pid2.identifier_type=5)&#xd;\n  left join form on(encounter.form_id=form.form_id and encounter.voided=0)&#xd;\n  left join users on(encounter.creator=users.user_id)&#xd;\n  left join person_name prs1 on(prs1.person_id=users.person_id and prs1.voided=0)&#xd;\n  left join person on(person.person_id=patient.patient_id)&#xd;\n  left join concept_name cn1 on(cn1.concept_id=obs.value_coded and cn1.locale=&apos;en&apos; and cn1.locale_preferred=1)&#xd;\n  LEFT JOIN global_property on(global_property.property=&apos;facility_datim_code&apos;)&#xd;\n where (encounter.form_id=13  AND encounter.voided=0 AND&#xd;\n encounter.encounter_datetime BETWEEN :startDate and :endDate) OR (person.dead=1 and person.death_date BETWEEN :startDate and :endDate and person.voided=0) GROUP BY patient.patient_id;&#xd;\n</sqlQuery>\n</org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition>', '2019-10-24 03:20:20', 1, '2020-09-28 11:52:40', 1, 0, NULL, NULL, NULL, 'a021b9b8-179a-4d62-9fbb-22181a8bad5e')/0xd
INSERT INTO `serialized_object` VALUES ('', 'Error-AddPastVisit', 'Encounters without visit', 'org.openmrs.module.reporting.dataset.definition.DataSetDefinition', 'org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition', 'org.openmrs.module.reporting.serializer.ReportingSerializer', '<org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition id=\"1\" uuid=\"157b39da-838c-41a6-9eb8-e39039bd0fc4\" retired=\"false\">\n  <name>Error-AddPastVisit</name>\n  <description>Encounters without visit</description>\n  <creator id=\"2\" uuid=\"1c3db49d-440a-11e6-a65c-00e04c680037\"/>\n  <dateCreated id=\"3\">2020-03-23 00:59:53 UTC</dateCreated>\n  <changedBy reference=\"2\"/>\n  <dateChanged id=\"4\">2020-04-07 16:37:18 UTC</dateChanged>\n  <parameters id=\"5\">\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"6\">\n      <name>startDate</name>\n      <label>startDate</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"7\">\n      <name>endDate</name>\n      <label>endDate</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n  </parameters>\n  <id>223</id>\n  <sqlQuery>select &#xd;\nencounter.patient_id,&#xd;\nencounter.visit_id as VisitID,&#xd;\npid1.identifier as PepfarID,&#xd;\npid2.identifier as HospID,&#xd;\nencounter.encounter_datetime as VisitDate,&#xd;\nform.name as PMMForm&#xd;\nfrom encounter &#xd;\nLEFT JOIN visit on(encounter.visit_id=visit.visit_id and encounter.voided=0 and visit.voided=0)&#xd;\nLEFT JOIN form on(form.form_id=encounter.form_id and encounter.voided=0)&#xd;\nLEFT JOIN patient_identifier pid1 on(pid1.patient_id=encounter.patient_id and pid1.identifier_type=4 and pid1.voided=0)&#xd;\nLEFT JOIN patient_identifier pid2 on(pid2.patient_id=encounter.patient_id and pid2.identifier_type=5 and pid2.voided=0)&#xd;\nwhere encounter.encounter_datetime BETWEEN :startDate and :endDate and encounter.voided=0 and encounter.visit_id is null;&#xd;\n</sqlQuery>\n</org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition>', '2020-03-23 01:59:53', 1, '2020-04-07 17:37:18', 1, 0, NULL, NULL, NULL, '157b39da-838c-41a6-9eb8-e39039bd0fc4')/0xd
INSERT INTO `serialized_object` VALUES ('', 'Error-DuplicateARTStartDate', 'Patients with more than one ART Start Date', 'org.openmrs.module.reporting.dataset.definition.DataSetDefinition', 'org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition', 'org.openmrs.module.reporting.serializer.ReportingSerializer', '<org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition id=\"1\" uuid=\"5021f513-bee5-4432-9d4c-a4ea29556330\" retired=\"false\">\n  <name>Error-DuplicateARTStartDate</name>\n  <description>Patients with more than one ART Start Date</description>\n  <creator id=\"2\" uuid=\"1c3db49d-440a-11e6-a65c-00e04c680037\"/>\n  <dateCreated id=\"3\">2020-03-23 01:00:46 UTC</dateCreated>\n  <changedBy reference=\"2\"/>\n  <dateChanged id=\"4\">2020-03-23 01:01:00 UTC</dateChanged>\n  <parameters id=\"5\">\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"6\">\n      <name>startDate</name>\n      <label>startDate</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"7\">\n      <name>endDate</name>\n      <label>endDate</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n  </parameters>\n  <id>224</id>\n  <sqlQuery>select &#xd;\nobs.person_id,&#xd;\npid1.identifier as PepfarID,&#xd;\npid2.identifier as HospID,&#xd;\ncount(obs.concept_id) as TotalEntries&#xd;\nfrom &#xd;\nobs &#xd;\nLEFT join patient_identifier pid1 on(pid1.patient_id=obs.person_id and pid1.identifier_type=4 and pid1.voided=0)&#xd;\nLEFT join patient_identifier pid2 on(pid2.patient_id=obs.person_id and pid2.identifier_type=5 and pid2.voided=0)  &#xd;\nwhere obs.concept_id=159599 and obs.voided=0 and obs.obs_datetime BETWEEN :startDate and :endDate  GROUP BY obs.person_id,obs.concept_id&#xd;\nHAVING count(obs.concept_id)&gt;1;&#xd;\n</sqlQuery>\n</org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition>', '2020-03-23 02:00:46', 1, '2020-03-23 02:01:00', 1, 0, NULL, NULL, NULL, '5021f513-bee5-4432-9d4c-a4ea29556330')/0xd
INSERT INTO `serialized_object` VALUES ('', 'Error-PharmacyFormsWithoutRegimenLineOrRegimen', 'Pharmacy forms without Regimen Line or Regimen', 'org.openmrs.module.reporting.dataset.definition.DataSetDefinition', 'org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition', 'org.openmrs.module.reporting.serializer.ReportingSerializer', '<org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition id=\"1\" uuid=\"d4c7fcdb-5a75-4313-9664-6784a6b65b5a\" retired=\"false\">\n  <name>Error-PharmacyFormsWithoutRegimenLineOrRegimen</name>\n  <description>Pharmacy forms without Regimen Line or Regimen</description>\n  <creator id=\"2\" uuid=\"1c3db49d-440a-11e6-a65c-00e04c680037\"/>\n  <dateCreated id=\"3\">2020-03-23 01:01:40 UTC</dateCreated>\n  <changedBy reference=\"2\"/>\n  <dateChanged id=\"4\">2020-04-07 16:36:47 UTC</dateChanged>\n  <parameters id=\"5\">\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"6\">\n      <name>startDate</name>\n      <label>startDate</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"7\">\n      <name>endDate</name>\n      <label>endDate</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n  </parameters>\n  <id>225</id>\n  <sqlQuery>select&#xd;\nobs.person_id,&#xd;\npid1.identifier as PepfarID,&#xd;\npid2.identifier as HospID,&#xd;\nform.name as PMMForm,&#xd;\nencounter.encounter_datetime as VisitDate,&#xd;\nMAX(IF(obs.concept_id=165708,cn1.name,null)) as RegimenLine,&#xd;\nMAX(IF(obs.concept_id=164506,cn1.name,null)) as Adult1stLineRegimens,&#xd;\nMAX(IF(obs.concept_id=164513,cn1.name,null)) as Adult2ndLineRegimens,&#xd;\nMAX(IF(obs.concept_id=165702,cn1.name,null)) as Adult3rdLineRegimens,&#xd;\nMAX(IF(obs.concept_id=164507,cn1.name,null)) as Child1stLineRegimens,&#xd;\nMAX(IF(obs.concept_id=164514,cn1.name,null)) as Child2ndLineRegimens,&#xd;\nMAX(IF(obs.concept_id=164703,cn1.name,null)) as Child3rdLineRegimens&#xd;\nfrom obs&#xd;\nLEFT join patient_identifier pid1 on(pid1.patient_id=obs.person_id and pid1.identifier_type=4 and pid1.voided=0)&#xd;\nLEFT join patient_identifier pid2 on(pid2.patient_id=obs.person_id and pid2.identifier_type=5 and pid2.voided=0)&#xd;\nINNER JOIN encounter on(encounter.encounter_id=obs.encounter_id and encounter.voided=0 and encounter.form_id=27)&#xd;\nLEFT JOIN form on(encounter.form_id=form.form_id and encounter.voided=0)&#xd;\nleft join concept_name cn1 on(obs.value_coded=cn1.concept_id and cn1.locale=&apos;en&apos; and cn1.locale_preferred=1)&#xd;\nWHERE encounter.encounter_datetime BETWEEN :startDate and :endDate and encounter.voided=0&#xd;\nGROUP BY &#xd;\nencounter.patient_id,&#xd;\nencounter.encounter_id&#xd;\nHAVING &#xd;\nMAX(IF(obs.concept_id=165708,cn1.name,null)) IS NULL&#xd;\nOR&#xd;\n(&#xd;\n    MAX(IF(obs.concept_id=164506,cn1.name,null)) IS NULL&#xd;\n    AND&#xd;\n    MAX(IF(obs.concept_id=164513,cn1.name,null)) IS NULL&#xd;\n    AND&#xd;\n    MAX(IF(obs.concept_id=165702,cn1.name,null)) IS NULL&#xd;\n    AND&#xd;\n    MAX(IF(obs.concept_id=164507,cn1.name,null)) IS NULL&#xd;\n    AND&#xd;\n    MAX(IF(obs.concept_id=164514,cn1.name,null)) IS NULL&#xd;\n    AND&#xd;\n    MAX(IF(obs.concept_id=164703,cn1.name,null)) IS NULL&#xd;\n);</sqlQuery>\n</org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition>', '2020-03-23 02:01:40', 1, '2020-04-07 17:36:47', 1, 0, NULL, NULL, NULL, 'd4c7fcdb-5a75-4313-9664-6784a6b65b5a')/0xd
INSERT INTO `serialized_object` VALUES ('', 'Error-DrugDuration', 'Pharmacy forms without drug duration or duration out of range', 'org.openmrs.module.reporting.dataset.definition.DataSetDefinition', 'org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition', 'org.openmrs.module.reporting.serializer.ReportingSerializer', '<org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition id=\"1\" uuid=\"f5f56e58-2e4c-44cb-b187-4d2dff419517\" retired=\"false\">\n  <name>Error-DrugDuration</name>\n  <description>Pharmacy forms without drug duration or duration out of range</description>\n  <creator id=\"2\" uuid=\"1c3db49d-440a-11e6-a65c-00e04c680037\"/>\n  <dateCreated id=\"3\">2020-03-23 01:02:46 UTC</dateCreated>\n  <changedBy reference=\"2\"/>\n  <dateChanged id=\"4\">2020-04-07 16:42:02 UTC</dateChanged>\n  <parameters id=\"5\">\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"6\">\n      <name>startDate</name>\n      <label>startDate</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"7\">\n      <name>endDate</name>\n      <label>endDate</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n  </parameters>\n  <id>226</id>\n  <sqlQuery>select &#xd;\nobs.person_id as patientID,&#xd;\nencounter.encounter_id,&#xd;\npid1.identifier as pepfarID,&#xd;\npid2.identifier as hospID,&#xd;\nperson.gender as Sex,&#xd;\nperson.birthdate as DoB,&#xd;\nTIMESTAMPDIFF(YEAR, person.birthdate,curdate()) as Age,&#xd;\nDATE_FORMAT(encounter.encounter_datetime,&apos;%d-%b-%Y&apos;) as DispenseDate,&#xd;\nform.name as PMMForm,&#xd;\nCONCAT(pn2.given_name,&apos;&apos;,pn2.family_name) as DispensedBy,&#xd;\nCONCAT(pn1.given_name,&apos;&apos;,pn1.family_name) as enteredBy,&#xd;\nencounter.date_created as dateEntered&#xd;\n,MAX(IF(obs.concept_id=165724,cn1.name, NULL)) as  `OIDrugName`&#xd;\n,MAX(IF(obs.concept_id=165727,cn1.name, NULL)) as  `ARVDrugName`&#xd;\n,MAX(IF(obs.concept_id=165304,cn1.name, NULL)) as  `TBDrugName`&#xd;\n,MAX(IF(obs.concept_id=165725,cn1.name, NULL)) as  `DrugStrength`&#xd;\n,MAX(IF(obs.concept_id=165723,cn1.name, NULL)) as  `DrugFrequency`&#xd;\n,MAX(IF(obs.concept_id=159368,obs.value_numeric, NULL)) as  `DrugDurationDays`&#xd;\n,MAX(IF(obs.concept_id=159368,obs.value_numeric, NULL)) as  `DrugQuantityPrescribedValue`&#xd;\n,MAX(IF(obs.concept_id=1443,obs.value_numeric, NULL)) as  `DrugQuantityDispensedValue`&#xd;\nfrom obs &#xd;\nleft join person on(person.person_id=obs.person_id and person.voided=0)&#xd;\nleft join patient_identifier pid1 on(pid1.patient_id=obs.person_id and pid1.identifier_type=4 and pid1.voided=0)&#xd;\nleft join patient_identifier pid2 on(pid2.patient_id=obs.person_id and pid2.identifier_type=5 and pid2.voided=0)&#xd;\nleft join encounter on(encounter.encounter_id=obs.encounter_id and encounter.voided=0)&#xd;\nleft join encounter_provider on(encounter_provider.encounter_id=encounter.encounter_id)&#xd;\nleft join users usr1 on(usr1.user_id=encounter.creator and encounter.voided=0)&#xd;\nleft join person_name pn1 on(usr1.person_id=pn1.person_id and pn1.voided=0)&#xd;\nleft join person_name pn2 on(encounter_provider.provider_id=pn2.person_id and pn2.voided=0)&#xd;\nleft join form on(form.form_id=encounter.form_id and encounter.voided=0)&#xd;\nleft join concept_name cn1 on(obs.value_coded=cn1.concept_id and cn1.locale=&apos;en&apos; and cn1.locale_preferred=1)&#xd;\nwhere encounter.form_id=27 and encounter.voided=0 and obs_group_id is not null &#xd;\nand encounter.encounter_datetime BETWEEN :startDate and :endDate&#xd;\nGROUP BY obs.person_id,encounter.encounter_id,obs.obs_group_id &#xd;\nHAVING&#xd;\n(&#xd;\nMAX(IF(obs.concept_id=165724,cn1.name, NULL)) IS NULL&#xd;\nAND&#xd;\nMAX(IF(obs.concept_id=165727,cn1.name, NULL)) IS NULL&#xd;\nAND&#xd;\nMAX(IF(obs.concept_id=165304,cn1.name, NULL)) IS NULL&#xd;\n)&#xd;\nOR&#xd;\nMAX(IF(obs.concept_id=159368,obs.value_numeric, NULL)) IS NULL&#xd;\nOR&#xd;\nMAX(IF(obs.concept_id=159368,obs.value_numeric, NULL))&gt;180&#xd;\n&#xd;\n&#xd;\n</sqlQuery>\n</org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition>', '2020-03-23 02:02:46', 1, '2020-04-07 17:42:02', 1, 0, NULL, NULL, NULL, 'f5f56e58-2e4c-44cb-b187-4d2dff419517')/0xd
INSERT INTO `serialized_object` VALUES ('', 'Error-IncompleteViralLoadData', 'Viral Loads without DateReported or DateOrdered', 'org.openmrs.module.reporting.dataset.definition.DataSetDefinition', 'org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition', 'org.openmrs.module.reporting.serializer.ReportingSerializer', '<org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition id=\"1\" uuid=\"808c3bed-1428-49f6-b4be-af751e76b54c\" retired=\"false\">\n  <name>Error-IncompleteViralLoadData</name>\n  <description>Viral Loads without DateReported or DateOrdered</description>\n  <creator id=\"2\" uuid=\"1c3db49d-440a-11e6-a65c-00e04c680037\"/>\n  <dateCreated id=\"3\">2020-03-23 01:04:07 UTC</dateCreated>\n  <changedBy reference=\"2\"/>\n  <dateChanged id=\"4\">2020-04-07 16:44:17 UTC</dateChanged>\n  <parameters id=\"5\">\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"6\">\n      <name>startDate</name>\n      <label>startDate</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"7\">\n      <name>endDate</name>\n      <label>endDate</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n  </parameters>\n  <id>227</id>\n  <sqlQuery>select&#xd;\nobs.person_id,&#xd;\npid1.identifier as PepfarID,&#xd;\npid2.identifier as HospID,&#xd;\nperson.gender as Sex,&#xd;\nperson.birthdate as DOB,&#xd;\nencounter.encounter_datetime as VisitDate,&#xd;\nform.name as PMMForm,&#xd;\nMAX(IF(obs.concept_id=164989,obs.value_datetime, null)) as DateOrdered,&#xd;\nMAX(IF(obs.concept_id=159951,obs.value_datetime, null)) as DateSampleCollected,&#xd;\nMAX(IF(obs.concept_id=165414,obs.value_datetime, null)) as DateReported,&#xd;\nMAX(IF(obs.concept_id=164980,cn1.name, null)) as IndicationForViralLoadTest,&#xd;\nMAX(IF(obs.concept_id=856,obs.value_numeric, null)) as ViralLoad&#xd;\nfrom &#xd;\nobs&#xd;\nleft join patient_identifier pid1 on(pid1.patient_id=obs.person_id and pid1.identifier_type=4 and pid1.voided=0)&#xd;\nleft join patient_identifier pid2 on(pid2.patient_id=obs.person_id and pid2.identifier_type=5 and pid2.voided=0)&#xd;\nINNER join encounter on(encounter.encounter_id=obs.encounter_id and encounter.voided=0 and encounter.form_id=21)&#xd;\nleft join person on(obs.person_id=person.person_id and obs.voided=0 and person.voided=0)&#xd;\nleft join concept_name cn1 on(obs.value_coded=cn1.concept_id and cn1.locale=&apos;en&apos; and cn1.locale_preferred=1)&#xd;\nleft join form on(form.form_id=encounter.form_id)&#xd;\nWHERE encounter.encounter_datetime BETWEEN :startDate and :endDate and encounter.voided=0&#xd;\nGROUP BY encounter.patient_id,encounter.encounter_id&#xd;\nHAVING &#xd;\nMAX(IF(obs.concept_id=856,obs.value_numeric, null)) IS NOT NULL&#xd;\nAND&#xd;\n(&#xd;\nMAX(IF(obs.concept_id=164989,obs.value_datetime, null)) IS NULL&#xd;\nOR&#xd;\nMAX(IF(obs.concept_id=165414,obs.value_datetime, null)) IS NULL&#xd;\n);&#xd;\n&#xd;\n</sqlQuery>\n</org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition>', '2020-03-23 02:04:07', 1, '2020-04-07 17:44:17', 1, 0, NULL, NULL, NULL, '808c3bed-1428-49f6-b4be-af751e76b54c')/0xd
