INSERT INTO `serialized_object` VALUES ('', 'HTS Client Intake ', 'Datasets from the HTS Client Intake Form', 'org.openmrs.module.reporting.dataset.definition.DataSetDefinition', 'org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition', 'org.openmrs.module.reporting.serializer.ReportingSerializer', '<org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition id=\"1\" uuid=\"53df7572-85dd-4dd1-8275-147faf8f9573\" retired=\"false\">\n  <name>HTS Client Intake </name>\n  <description>Datasets from the HTS Client Intake Form</description>\n  <creator id=\"2\" uuid=\"1c3db49d-440a-11e6-a65c-00e04c680037\"/>\n  <dateCreated id=\"3\">2020-09-28 04:46:28 UTC</dateCreated>\n  <changedBy reference=\"2\"/>\n  <dateChanged id=\"4\">2020-09-28 04:50:07 UTC</dateChanged>\n  <parameters id=\"5\">\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"6\">\n      <name>startDate</name>\n      <label>Start Date</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"7\">\n      <name>endDate</name>\n      <label>End Date</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n  </parameters>\n  <id>233</id>\n  <sqlQuery>SELECT&#xd;\n   global_property.property_value as DatimCode,&#xd;\n   pid1.identifier as HTSClientCode,&#xd;\n   pid2.identifier as PepfarID,&#xd;\n   pid3.identifier as HospID,&#xd;\n   pid4.identifier as RecencyID,&#xd;\n   DATE_FORMAT(enc.encounter_datetime,&apos;%d-%b-%Y&apos;) as VisitDate,&#xd;\n   visit.visit_id as VisitID,&#xd;\n   MAX(IF(obs.concept_id=166136,cn1.name, NULL))&#xd;\n   as `KindOfHTS`,&#xd;\n   MAX(IF(obs.concept_id=165839,cn1.name, NULL))&#xd;\n   as `Setting`,&#xd;\n   MAX(IF(obs.concept_id=165966,obs.value_text, NULL))&#xd;\n   as `SettingOthersSpecify`,&#xd;\n   MAX(IF(obs.concept_id= 165790,cn1.name, NULL))&#xd;\n   as `FirstTimeVisit`,&#xd;\n   MAX(IF(obs.concept_id= 165793,cn1.name, NULL)) &#xd;\n   as `TypeOfSession`,&#xd;\n   MAX(IF(obs.concept_id= 165480,cn1.name, NULL)) &#xd;\n   as `ReferredFrom`,&#xd;\n   MAX(IF(obs.concept_id= 1054,cn1.name, NULL)) &#xd;\n   as `MaritalStatus`,&#xd;\n   MAX(IF(obs.concept_id= 160312,obs.value_numeric, NULL))&#xd;\n   as `NoOfOwnChildrenLessThan5`,&#xd;\n   MAX(IF(obs.concept_id=5557,obs.value_numeric, NULL))&#xd;\n   as `NoOfWives`,&#xd;\n   MAX(IF(obs.concept_id=165794,cn1.name, NULL))&#xd;\n   as `IsClientIdentifiedFromAnindexClient`,&#xd;\n   MAX(IF(obs.concept_id= 165798,cn1.name, NULL))&#xd;\n   as IndexType,&#xd;\n   MAX(IF(obs.concept_id=165859,obs.value_text, NULL))&#xd;\n   as IndexClientID,&#xd;\n   MAX(IF(obs.concept_id=165976,cn1.name, NULL)) as &#xd;\n   `IsClientRetestingForResultVerification`,&#xd;\n   MAX(IF(obs.concept_id=166136,cn1.name, NULL)) as &#xd;\n   `PreviouslyTestedHIVNegative`,&#xd;\n   MAX(IF(obs.concept_id=1434,cn1.name, NULL)) as &#xd;\n   `ClientPregnant`,&#xd;\n   MAX(IF(obs.concept_id= 165801,cn1.name, NULL)) as &#xd;\n   `ClientInformedAboutHIVTransmissionRoutes`,&#xd;\n   MAX(IF(obs.concept_id= 165802,cn1.name, NULL)) as &#xd;\n   `ClientInformedAboutRiskFactorsForHIVTransmission`,&#xd;\n   MAX(IF(obs.concept_id= 165804,cn1.name, NULL)) as &#xd;\n   `ClientInformedOnPreventingHIVTransmissionMethods`,&#xd;\n   MAX(IF(obs.concept_id= 165884,cn1.name, NULL)) as &#xd;\n   `ClientInformedAboutPossibleTestResults`,&#xd;\n   MAX(IF(obs.concept_id= 1710,cn1.name, NULL)) as &#xd;\n   `InformedConsentForHivTestingGiven`,&#xd;\n   MAX(IF(obs.concept_id=159218,cn1.name, NULL)) as &#xd;\n   `EverHadSexualIntercourse`,&#xd;\n   MAX(IF(obs.concept_id=159218,cn1.name, NULL)) as&#xd;\n   `BloodTransfusioninLast3Months`,&#xd;\n   MAX(IF(obs.concept_id=159218,cn1.name, NULL)) as &#xd;\n   `UnprotectedSexWithCasualPartnerinLast3Months`,&#xd;\n   MAX(IF(obs.concept_id=165803,cn1.name, NULL))&#xd;\n   as `UnprotectedSexWithRegularPartnerInTheLast3Months`,&#xd;\n   MAX(IF(obs.concept_id=164809,cn1.name, NULL))&#xd;\n   as `STIinLast3Months`,&#xd;\n   MAX(IF(obs.concept_id=165806,cn1.name, NULL)) &#xd;\n   as `MoreThan1SexPartnerDuringLast3Months`,&#xd;\n   MAX(IF(obs.concept_id=143264,cn1.name, NULL)) as `CurrentCough`,&#xd;\n   MAX(IF(obs.concept_id=832,cn1.name, NULL)) as `WeightLoss`,&#xd;\n   MAX(IF(obs.concept_id=140238,cn1.name, NULL)) as `Fever`,&#xd;\n   MAX(IF(obs.concept_id=133027,cn1.name, NULL)) as  `NightSweats`,&#xd;\n   MAX(IF(obs.concept_id=124068,cn1.name, NULL)) as  `ContactWithTBPatient`,&#xd;\n   MAX(IF(obs.concept_id=165808,obs.value_numeric, NULL)) as  `TBScreeningScore`,&#xd;\n   MAX(IF(obs.concept_id=165809,cn1.name, NULL)) as &#xd;\n   `ComplaintsofVaginalDischargeorBurningWhenUrinating`,&#xd;\n   MAX(IF(obs.concept_id=165810,cn1.name, NULL)) as &#xd;\n   `ComplaintsofLowerAbdominalPainsWithorWithoutVaginalDischarge`,&#xd;\n   MAX(IF(obs.concept_id=165813,cn1.name, NULL)) as &#xd;\n   `ComplaintsofGenitalSoreOrSwollenInguinalLymphNodesWithorWithoutPains`,&#xd;\n   MAX(IF(obs.concept_id=165814,obs.value_numeric, NULL)) as &#xd;\n   `STIScreeningScore`,&#xd;\n  MAX(IF(obs.concept_id=165807,obs.value_numeric, NULL)) as `RiskAssesmentScore`,&#xd;\n   MAX(IF(obs.concept_id=165840,cn1.name, NULL)) as `ScreeningTestResult`,&#xd;\n   MAX(IF(obs.concept_id=165844,obs.value_datetime, NULL)) as `ScreeningTestDate`,&#xd;\n   MAX(IF(obs.concept_id=165841,cn1.name, NULL)) as `ConfirmatoryTestResult`,&#xd;\n  MAX(IF(obs.concept_id=165845,cn1.name, NULL)) as  `ConfirmatoryTestDate`,&#xd;\n  MAX(IF(obs.concept_id=165842,cn1.name, NULL)) as `TieBreaker`,&#xd;\n  MAX(IF(obs.concept_id=165846,obs.value_datetime, NULL)) as `TieBreakerDate`,&#xd;\n   MAX(IF(obs.concept_id=165843,cn1.name, NULL)) as `FinalResult`,&#xd;\n   MAX(IF(obs.concept_id=165881,cn1.name, NULL)) as  `HaveYouBeenTestedforHivBeforeWithinThisYear`,&#xd;\n   MAX(IF(obs.concept_id=165818,cn1.name, NULL)) as `HIVRequestandResultFormSignedbyTester`,&#xd;\n   MAX(IF(obs.concept_id=164848,cn1.name, NULL)) as `ClientReceivedHIVTestResult`,&#xd;\n   MAX(IF(obs.concept_id=159382,cn1.name, NULL)) as `PostTestCounselingDone`,&#xd;\n   MAX(IF(obs.concept_id=165883,cn1.name, NULL)) as  `ClientUseFPMethodsOtherThanCondom`,&#xd;\n   MAX(IF(obs.concept_id=5571,cn1.name, NULL)) as  ClientUseCondomsAsFPMethod,&#xd;\n   MAX(IF(obs.concept_id=299,cn1.name, NULL)) as SyphilisTestResult,&#xd;\n    MAX(IF(obs.concept_id=159430,cn1.name, NULL)) as  HepatitisBVirusTestResult,&#xd;\n    MAX(IF(obs.concept_id=161471,cn1.name, NULL)) as HepatitisCVirusTestResult,&#xd;\n    MAX(IF(obs.concept_id=1648,cn1.name, NULL)) as ClientReferredtoOtherServices,&#xd;\n  MAX(IF(obs.concept_id=165820,cn1.name, NULL)) as  RiskReductionPlanDeveloped,&#xd;\n    MAX(IF(obs.concept_id=165821,cn1.name, NULL)) as PostTestDisclosurePlanDeveloped,&#xd;\n    MAX(IF(obs.concept_id=165822,cn1.name, NULL)) as WillBringPartnerForHivTesting,&#xd;\n    MAX(IF(obs.concept_id=165823,cn1.name, NULL)) as WillBringOwnChildrenLessThan5YearsforHIVTesting,&#xd;\n    MAX(IF(obs.concept_id=165841,cn1.name, NULL)) as  ProvidedWithInformationonFPandDualContraception,&#xd;\n   MAX(IF(obs.concept_id=1382,cn1.name, NULL)) as CorrectCondomUseDemonstrated,&#xd;\n    MAX(IF(obs.concept_id=159777,cn1.name, NULL)) as CondomsProvidedtoClient,&#xd;\n    MAX(IF(obs.concept_id=165045,obs.value_text, NULL)) as AdditionalComments,&#xd;\n	MAX(IF(obs.concept_id=166242,obs.value_datetime, NULL)) as SignatureDate&#xd;\n  FROM&#xd;\n   obs&#xd;\n   LEFT JOIN patient ON(patient.patient_id=obs.person_id and obs.voided=0 and patient.voided=0)&#xd;\n   LEFT join encounter enc on(enc.encounter_id=obs.encounter_id and enc.voided=0 and obs.voided=0 and enc.form_id=10)&#xd;\n   LEFT JOIN visit on (enc.visit_id=visit.visit_id and visit.voided=0)&#xd;\n   left join concept_name cn1 on(obs.value_coded=cn1.concept_id and cn1.locale=&apos;en&apos; and cn1.locale_preferred=1)&#xd;\n   LEFT JOIN patient_identifier pid1 on(pid1.patient_id=patient.patient_id and patient.voided=0 and pid1.identifier_type=8 and pid1.voided=0)&#xd;\n   LEFT JOIN patient_identifier pid2 on(pid2.patient_id=patient.patient_id and patient.voided=0 and pid2.identifier_type=4 and pid2.voided=0)&#xd;\n   LEFT JOIN patient_identifier pid3 on(pid3.patient_id=patient.patient_id and patient.voided=0 and pid3.identifier_type=5 and pid3.voided=0)&#xd;\n   LEFT JOIN patient_identifier pid4 on(pid4.patient_id=patient.patient_id and patient.voided=0 and pid4.identifier_type=10 and pid4.voided=0)&#xd;\n   LEFT JOIN global_property on(global_property.property=&apos;facility_datim_code&apos;)&#xd;\n   WHERE patient.voided=0 and enc.voided=0 and enc.encounter_datetime BETWEEN :startDate and :endDate&#xd;\n   GROUP BY patient.patient_id, enc.encounter_id;&#xd;\n   &#xd;\n</sqlQuery>\n</org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition>', '2020-09-28 05:46:28', 1, '2020-09-28 05:50:07', 1, 0, NULL, NULL, NULL, '53df7572-85dd-4dd1-8275-147faf8f9573')/0xd
INSERT INTO `serialized_object` VALUES ('', 'General Antenatal ', 'Dataset from the General Antenatal Form in NMRS representing the General Antenatal REgister.', 'org.openmrs.module.reporting.dataset.definition.DataSetDefinition', 'org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition', 'org.openmrs.module.reporting.serializer.ReportingSerializer', '<org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition id=\"1\" uuid=\"e38449f1-7e54-448e-8d68-ca81c8869f49\" retired=\"false\">\n  <name>General Antenatal </name>\n  <description>Dataset from the General Antenatal Form in NMRS representing the General Antenatal REgister.</description>\n  <creator id=\"2\" uuid=\"1c3db49d-440a-11e6-a65c-00e04c680037\"/>\n  <dateCreated id=\"3\">2020-09-28 04:54:20 UTC</dateCreated>\n  <changedBy reference=\"2\"/>\n  <dateChanged id=\"4\">2020-09-28 04:55:23 UTC</dateChanged>\n  <parameters id=\"5\">\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"6\">\n      <name>startDate</name>\n      <label>Start Date</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"7\">\n      <name>endDate</name>\n      <label>End Date</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n  </parameters>\n  <id>234</id>\n  <sqlQuery>SELECT&#xd;\n   global_property.property_value as DatimCode,&#xd;\n   pid1.identifier as ANCNo,&#xd;\n   pid2.identifier as PepfarID,&#xd;\n   pid3.identifier as HospID,&#xd;\nDATE_FORMAT(enc.encounter_datetime,&apos;%d-%b-%Y&apos;) &#xd;\nas VisitDate,&#xd;\nvisit.visit_id as VisitID,&#xd;\nMAX(IF(obs.concept_id=1427,DATE_FORMAT(obs.value_datetime,&apos;%d-%b-%Y&apos;), NULL))&#xd;\n   as `DateOfLastMenstralPeriod`,&#xd;\n   MAX(IF(obs.concept_id=1438,obs.value_numeric, NULL))&#xd;\n   as `GestationAgeWeeks`,&#xd;\n   MAX(IF(obs.concept_id=5624,obs.value_numeric, NULL))&#xd;\n   as `Gravida`,&#xd;\n   MAX(IF(obs.concept_id=5596,DATE_FORMAT(obs.value_datetime,&apos;%d-%b-%Y&apos;), NULL))&#xd;\n   as `ExpectedDeliveryDate`,&#xd;\n   MAX(IF(obs.concept_id=1053,obs.value_numeric, NULL)) &#xd;\n   as `Parity`,&#xd;\n   MAX(IF(obs.concept_id=1053,obs.value_numeric, NULL)) &#xd;\n   as `Losses`,&#xd;\n   MAX(IF(obs.concept_id=165280,cn1.name, NULL)) &#xd;\n   as `WomanTestedForSyphilis`,&#xd;\n   MAX(IF(obs.concept_id= 164952,DATE_FORMAT(obs.value_datetime,&apos;%d-%b-%Y&apos;), NULL))&#xd;\n   as `SyphilisTestDate`,&#xd;\n   MAX(IF(obs.concept_id= 299,cn1.name, NULL))&#xd;\n   as `SyphilisTestResult`,&#xd;\n   MAX(IF(obs.concept_id= 160733,cn1.name, NULL))&#xd;\n   as `TreatedForSyphilis`,&#xd;\n   MAX(IF(obs.concept_id= 164953,DATE_FORMAT(obs.value_datetime,&apos;%d-%b-%Y&apos;), NULL))&#xd;\n   as `SyphilisTreatmentDate`,&#xd;\n   MAX(IF(obs.concept_id= 160312,cn1.name, NULL))&#xd;\n   as `ReferredSyphilisPositiveClient`,&#xd;\n   MAX(IF(obs.concept_id= 159430,cn1.name, NULL))&#xd;\n   as `HepatitisBTest`,&#xd;\n   MAX(IF(obs.concept_id= 161471,cn1.name, NULL))&#xd;\n   as `HepatitisCTest`&#xd;\n   &#xd;\n  FROM&#xd;\n   obs&#xd;\n   LEFT JOIN patient ON(patient.patient_id=obs.person_id and obs.voided=0 and patient.voided=0)&#xd;\n   LEFT join encounter enc on(enc.encounter_id=obs.encounter_id and enc.voided=0 and obs.voided=0 and enc.form_id=16)&#xd;\n   LEFT JOIN visit on (enc.visit_id=visit.visit_id and visit.voided=0)&#xd;\n   left join concept_name cn1 on(obs.value_coded=cn1.concept_id and cn1.locale=&apos;en&apos; and cn1.locale_preferred=1)&#xd;\n   LEFT JOIN patient_identifier pid1 on(pid1.patient_id=patient.patient_id and patient.voided=0 and pid1.identifier_type=6 and pid1.voided=0)&#xd;\n   LEFT JOIN patient_identifier pid2 on(pid2.patient_id=patient.patient_id and patient.voided=0 and pid2.identifier_type=4 and pid2.voided=0)&#xd;\n   LEFT JOIN patient_identifier pid3 on(pid3.patient_id=patient.patient_id and patient.voided=0 and pid3.identifier_type=5 and pid3.voided=0)&#xd;\n   LEFT JOIN global_property on(global_property.property=&apos;facility_datim_code&apos;)&#xd;\n   WHERE patient.voided=0 &#xd;\n   and enc.voided=0 and &#xd;\n   enc.encounter_datetime BETWEEN&#xd;\n   :startDate and :endDate&#xd;\n   GROUP BY patient.patient_id, enc.encounter_id;&#xd;\n   &#xd;\n</sqlQuery>\n</org.openmrs.module.reporting.dataset.definition.SqlDataSetDefinition>', '2020-09-28 05:54:20', 1, '2020-09-28 05:55:23', 1, 0, NULL, NULL, NULL, 'e38449f1-7e54-448e-8d68-ca81c8869f49')/0xd
